/**
* APIInterface module for interfacing with the LibreSignage HTTP API.
*
* @module libresignage/APIInterface
*/

/**
* API endpoint definitions.
*/
exports.APIEndpoints = require('./APIEndpoints.js').APIEndpoints;

/**
* APIInterface object definition.
* @see APIInterface
*/
exports.APIInterface = require('./APIInterface.js').APIInterface;

/**
* APIError object definition.
* @see APIError
*/
exports.APIError = require('./APIError.js').APIError;

/**
* HTTP status codes.
*/
exports.HTTPStatus = require('./HTTPStatus.js').HTTPStatus;

// Add the various API classes to window.* for debugging.
if (typeof window !== 'undefined') { Object.assign(window, exports); }
