var AssertError = require('libresignage/util/assert/AssertError');

/**
* A simple assert implementation for LibreSignage.
*
* @module libresignage/util/assert/Assert.
*/
module.exports = class Assert {
	/**
	* Perform an assertion.
	*
	* @param {*}      condition The condition to test.
	* @param {string} msg       An optional message to print when the
	*                           assertion fails. (default: "Assertion failed!")
	*
	* @throws {AssertError} If condition != true.
	*/
	static assert(condition, msg) {
		if (!condition && msg) {
			throw new AssertError(msg);
		} else if (!condition) {
			throw new AssertError('Assertion failed!');
		}
	}
}
