/**
* A class representing an assertion error.
*
* @memberof module:libresignage/util/assert
*/
class AssertError extends Error {
	/**
	* Construct an AssertError object.
	*
	* @constructor
	*
	* @param {string} message A message to use for the error.
	* @param {string} file    The filename where the error occured.
	* @param {number} line    The line number where the error occured.
	*/
	constructor(message, file, line) {
		super(message, file, line);
		if (Error.captureStackTrace) {
			Error.captureStackTrace(this, AssertError);
		}
	}
}
module.exports = AssertError;
